mapper.insert:
insert into calendar(
	 id             
	,groupId        
	,title          
	,writer         
	,content        
	,startDate      
	,endDate        
	,allDay         
	,textColor      
	,backgroundColor
	,borderColor
) values (
	 (select lpad(ifnull(max(a.id),0)+1, 4, 0) as id from calendar a)
	,(select concat('GRP', lpad(ifnull(max(replace(a.groupId,'GRP','')),0) + 1, 4, 0)) from calendar a)
	,#{title}
	,#{writer}
	,#{content}
	,str_to_date(#{start}, '%Y-%m-%d %H:%i')
	,str_to_date(#{end}  , '%Y-%m-%d %H:%i')
	,#{allDay}
	,#{textColor}
	,#{backgroundColor}
	,#{borderColor}
)

mapper.update:
 		update calendar
 		   set title           = #{title}
			  ,writer          = #{writer}
			  ,content         = #{content}
			  ,startDate       = str_to_date(#{start}, '%Y-%m-%d %H:%i')
			  ,endDate         = str_to_date(#{end}  , '%Y-%m-%d %H:%i')
			  ,allDay          = #{allDay}
			  ,textColor       = #{textColor}
			  ,backgroundColor = #{backgroundColor}
			  ,borderColor     = #{borderColor}
		 where id = #{id}
