mapper.select:
select (count(*) + 1) as fileCommonKey 
  from tb_file_mng 
 where refWord = #{refWord}

select row_number() over(order by a.uploadDt desc) as rownum ,a.uuid ,a.content ,substring_index(a.originName,'.',1) as originName ,a.fileExt ,a.storedFileNm ,a.filePath ,case when a.size < 1024 then 1 else round(a.size/1024) end as size ,a.refWord ,a.refKey ,a.uploadBy ,a.uploadDt from tb_file_mng a where 1=1
and a.uuid = #{uuid}
and a.content like concat('%', #{content}, '%')
and a.refWord = #{refWord}
and a.refKey = #{refKey}
and a.originName like concat('%', #{sch_fileName}, '%')
and a.uploadBy like concat('%', #{sch_uploadBy}, '%')
and #{sch_startDate} <= DATE_FORMAT(a.uploadDt, '%Y-%m-%d')
and DATE_FORMAT(a.uploadDt, '%Y-%m-%d') <= #{sch_endDate}
order by a.uploadDt desc
