mapper.select:
with recursive MonthByDay as (
select last_day(now() - interval 1 month) + interval 1 day as DT union all
select DT + interval 1 day from MonthByDay where DT < last_day(now()) ), LogCount as ( select DATE_FORMAT(a.DT, '%d') as DT, (select count(id) from user_connect_log_history where a.DT = DATE_FORMAT(CONNECT_DT ,'%Y-%m-%d') and id = #{userId}) as CNT from MonthByDay a ) select * from LogCount;
